<script>
import {TweenMax, TimelineMax} from 'gsap'

export default {
    name: 'Waves',
    data: function() {
        return {
            isAnimating: false,
        }
    },
    methods: {
        animate: function() {
            if (!this.isAnimating) {
                this.isAnimating = true
                var t1 = new TimelineMax()
                t1.to(this.$refs.wave1, 3, {
                    x: '+=45',
                    ease: Power1.easeOut,
                })
                    .to(this.$refs.wave1, 3, {
                        x: 0,
                        ease: Power1.easeIn,
                    })

                var t2 = new TimelineMax()
                t2.to(this.$refs.wave2, 3, {
                    x: '-=45',
                    ease: Power1.easeOut,
                })
                    .to(this.$refs.wave2, 3, {
                        x: 0,
                        ease: Power1.easeIn,
                    })

                var t3 = new TimelineMax()
                t3.to(this.$refs.wave3, 3, {
                    x: '+=45',
                    ease: Power1.easeOut,
                })
                    .to(this.$refs.wave3, 3, {
                        x: 0,
                        ease: Power1.easeIn,
                    })

                var t4 = new TimelineMax()
                t4.to(this.$refs.wave4, 3, {
                    x: '-=45',
                    ease: Power1.easeOut,
                })
                    .to(this.$refs.wave4, 3, {
                        x: 0,
                        ease: Power1.easeIn,
                        onComplete: () => {
                            this.isAnimating = false
                        }
                    })

                var master = new TimelineMax()
                master.add(t1, 0.1)
                master.add(t2, 0.1)
                master.add(t3, 0.1)
                master.add(t4, 0.1)
            }
        },
    },
    mounted: function() {
        this.animate()
    }
}
</script>

